package javaPackage;

public class ConditionalStatemnet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =21;
		int b=	a%2;
		if(b == 0)
		{
			System.out.println("The number is even "+a);
		}
		else
		{
			System.out.println("The number is odd "+a);
		}
	}

}
